const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:8000";

export async function postLocation(payload) {
  try {
    console.log("‚û°Ô∏è Sending to:", `${API_BASE}/capture_location`, payload);

    const res = await fetch(`${API_BASE}/capture_location`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(payload),
    });

    if (!res.ok) {
      const errorText = await res.text();
      console.error("‚ùå Backend responded with error:", res.status, errorText);
      throw new Error(`HTTP error! status: ${res.status}`);
    }

    const data = await res.json();
    console.log("‚úÖ Backend response:", data);
    return data;
  } catch (err) {
    console.error("üö® API call failed (network/CORS/backend):", err.message);
    throw err;
  }
}
