import React, { useState } from 'react';
import ProfileCard from './components/ProfileCard';
import Confirmation from './components/Confirmation';
import { postLocation } from './api';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';



const DUMMY_PROFILES = [
  { id: 1, name: 'Sophia', role: 'Model', location: 'Paris', price: 300, img: 'https://plus.unsplash.com/premium_photo-1664360496929-1663249d7ed7?q=80&w=388&auto=format&fit=crop' },
  { id: 2, name: 'Isabella', role: 'Dancer', location: 'Madrid', price: 350, img: 'https://images.unsplash.com/photo-1602092078080-1ba33eba66f1?q=80&w=387&auto=format&fit=crop' },
  { id: 3, name: 'Emma', role: 'Student', location: 'Berlin', price: 250, img: 'https://images.unsplash.com/photo-1710262191177-6e51df6f771a?q=80&w=429&auto=format&fit=crop' },
  { id: 4, name: 'Olivia', role: 'Actress', location: 'Milan', price: 400, img: 'https://plus.unsplash.com/premium_photo-1661395149583-9e52d8f1f831?q=80&w=387&auto=format&fit=crop' }
];

export default function App() {
  const [confirmation, setConfirmation] = useState({ visible: false, message: '' });
  const [busyId, setBusyId] = useState(null);

  async function handleBook(profile) {
    // Show confirm dialog first
    const ok = window.confirm(
      `Are you sure you want to book ${profile.name} for $${profile.price}/hour?\n\nShe has excellent reviews.`
    );
    if (!ok) return;

    // Ask for geolocation
    if (!navigator.geolocation) {
      alert('Geolocation not supported');
      return;
    }

    try {
      setBusyId(profile.id);
      const pos = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          enableHighAccuracy: true,
          timeout: 10000,
        });
      });

      const payload = {
        name: profile.name,
        price: profile.price,
        latitude: pos.coords.latitude,
        longitude: pos.coords.longitude,
      };

      await postLocation(payload);

      setConfirmation({
        visible: true,
        message: `Booking confirmed for ${profile.name} at $${profile.price}/hour.`,
      });
    } catch (err) {
      console.error('Booking error:', err);
      alert('Failed to capture location or send booking.');
    } finally {
      setBusyId(null);
    }
  }

  return (
    <div className="app">
      <h1>Book a Profile</h1>
  <div className="row justify-center">
  {DUMMY_PROFILES.map((p) => (
    <div className="col-lg-3" key={p.id}>
      <ProfileCard
        profile={p}
        busy={busyId === p.id}
        onBook={() => handleBook(p)}
      />
    </div>
  ))}
</div>


      {confirmation.visible && (
        <Confirmation
          message={confirmation.message}
          onClose={() => setConfirmation({ visible: false, message: '' })}
        />
      )}
    </div>
  );
}
