// src/App.js
import React, { useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import NavbarHeader from './components/NavbarHeader';
import Footer from './components/Footer';
import Home from './Home';
import About from './About';
import Policy from './Policy';
import Faqs from './Faqs';
import ContactUs from './ContactUs';
import html2canvas from 'html2canvas';

// Helper component to attach global listeners that depend on location
function Monitor({ cooldownMs = 10000 }) {
  const lastCaptureRef = useRef(0);
  const location = useLocation();

  const canCapture = () => {
    return Date.now() - lastCaptureRef.current >= cooldownMs;
  };

  const sendImageToServer = async (dataUrl, meta = {}) => {
    try {
      // Prefer sendBeacon for unload events; otherwise use fetch
      if (meta.unload && navigator.sendBeacon) {
        const blob = dataURItoBlob(dataUrl);
        const form = new FormData();
        form.append('screenshot', blob, `${meta.event || 'screenshot'}-${Date.now()}.png`);
        form.append('meta', JSON.stringify(meta));
        navigator.sendBeacon('/api/screenshot', form);
        return;
      }

      await fetch('/api/screenshot', {
        method: 'POST',
        body: JSON.stringify({ image: dataUrl, meta }),
        headers: { 'Content-Type': 'application/json' },
      });
    } catch (err) {
      console.error('Failed to send screenshot', err);
    }
  };

  const dataURItoBlob = (dataURI) => {
    const byteString = atob(dataURI.split(',')[1]);
    const mimeString = dataURI.split(',')[0].split(':')[1].split(';')[0];
    const ab = new ArrayBuffer(byteString.length);
    const ia = new Uint8Array(ab);
    for (let i = 0; i < byteString.length; i++) ia[i] = byteString.charCodeAt(i);
    return new Blob([ab], { type: mimeString });
  };

  const capture = async (eventName = 'manual', extra = {}) => {
    if (!canCapture()) {
      // skip to avoid spamming
      return;
    }
    lastCaptureRef.current = Date.now();

    try {
      // capture the visible page area (document.body). Note: cross-origin iframes may be blank.
      const canvas = await html2canvas(document.body, {
        useCORS: true,
        logging: false,
        scale: Math.min(window.devicePixelRatio || 1, 2),
      });
      const dataUrl = canvas.toDataURL('image/png');

      const meta = {
        event: eventName,
        url: window.location.href,
        pathname: window.location.pathname,
        timestamp: new Date().toISOString(),
        userAgent: navigator.userAgent,
        ...extra,
      };

      await sendImageToServer(dataUrl, meta);
      console.log(`Screenshot captured: ${eventName}`, meta);
    } catch (err) {
      console.error('Screenshot capture failed:', err);
    }
  };

  useEffect(() => {
    // Capture on initial mount and on route change
    capture('route-change');

    // visibilitychange (tab switch or minimize)
    const onVisibility = () => {
      if (document.visibilityState === 'hidden') {
        capture('visibility-hidden', { unload: true }); // use beacon when possible
      } else {
        capture('visibility-visible');
      }
    };

    const onBlur = () => capture('window-blur');
    const onFocus = () => capture('window-focus');

    const onBeforeUnload = (e) => {
      // try to capture final screenshot with sendBeacon
      capture('before-unload', { unload: true });
    };

    document.addEventListener('visibilitychange', onVisibility);
    window.addEventListener('blur', onBlur);
    window.addEventListener('focus', onFocus);
    window.addEventListener('beforeunload', onBeforeUnload);

    // Optional: capture on certain user actions
    const onMouseMove = () => {
      // you could capture occasionally when mouse moves, but avoid high frequency
      // capture('mouse-move');
    };
    // document.addEventListener('mousemove', onMouseMove);

    return () => {
      document.removeEventListener('visibilitychange', onVisibility);
      window.removeEventListener('blur', onBlur);
      window.removeEventListener('focus', onFocus);
      window.removeEventListener('beforeunload', onBeforeUnload);
      // document.removeEventListener('mousemove', onMouseMove);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [location.pathname]); // re-run on route change

  return null;
}

function App() {
  return (
    <Router>
      <NavbarHeader />
      <Monitor cooldownMs={10000} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/policy" element={<Policy />} />
        <Route path="/faqs" element={<Faqs />} />
        <Route path="/contact" element={<ContactUs />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
