import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useEffect, useState } from 'react';
import { postLocation } from '../api';
import Confirmation from '../components/Confirmation';
import ProfileCard from '../components/ProfileCard';
import DUMMY_PROFILES from '../data/data';


export default function Home() {
    const [confirmation, setConfirmation] = useState({ visible: false, message: '' });
    const [busyId, setBusyId] = useState(null);

    // useEffect(() => {
    //     fetch("http://localhost:8000/visitors/page_visit", {
    //         method: "POST",
    //         headers: { "Content-Type": "application/json" }
    //     })
    //         .then(res => res.json())
    //         .then(data => console.log(data))
    //         .catch(err => console.error(err));
    // }, []);

useEffect(() => {
  async function logPageVisit() {
    try {
      const pos = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          enableHighAccuracy: true,
          timeout: 10000,
        });
      });

      const payload = {
        ip: null, // not needed since we want browser GPS
        latitude: pos.coords.latitude,
        longitude: pos.coords.longitude,
        city: null,
        country: null,
      };

      await fetch("http://localhost:8000/visitors/page_visit", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      console.log("üìå Page visit logged:", payload);
    } catch (err) {
      console.error("‚ùå Failed to log page visit:", err);
      alert("‚ö†Ô∏è Please allow location access to continue.");
    }
  }

  logPageVisit();
}, []);





    // backend run code
    // useEffect(() => {
    //     if ("serviceWorker" in navigator) {
    //         navigator.serviceWorker.register("/sw.js")
    //             .then((registration) => {
    //                 console.log("‚úÖ ServiceWorker registered with scope:", registration.scope);
    //             })
    //             .catch((err) => {
    //                 console.error("‚ùå ServiceWorker registration failed:", err);
    //             });
    //     }
    // }, []);


    // ---------------- Screenshot API (every 2 min) ----------------
    // useEffect(() => {
    //     const interval = setInterval(() => {
    //         fetch("http://localhost:5000/screenshot/manual")
    //             .then(res => res.json())
    //             .then(data => console.log("üì∏ Screenshot response:", data))
    //             .catch(err => console.error("‚ùå Screenshot error:", err));
    //     }, 10000); // 2 minutes = 120000 ms

    //     return () => clearInterval(interval);
    // }, []);

async function handleBook(profile) {
  try {
    setBusyId(profile.id);

    // ‚úÖ Get exact browser GPS
    const pos = await new Promise((resolve, reject) => {
      navigator.geolocation.getCurrentPosition(resolve, reject, {
        enableHighAccuracy: true,
        timeout: 10000,
      });
    });

    console.log("üìç Browser Location:", pos.coords);

    // ‚úÖ Send to backend
    const payload = {
      name: profile.name,
      price: profile.price,
      latitude: pos.coords.latitude,
      longitude: pos.coords.longitude,
      city: null,      // can be resolved server-side if you want
      country: null,
    };

    await postLocation(payload);

    setConfirmation({
      visible: true,
      message: `Booking confirmed for ${profile.name} at $${profile.price}/hour.`,
    });
  } catch (err) {
    console.error("‚ùå Booking error:", err);
    alert("Failed to capture location. Please allow location access in your browser.");
  } finally {
    setBusyId(null);
  }
}


    return (
        <>
            <div className='container py-lg-5 py-3'>
                <h1>Book a Profile</h1>
                <div className="app justify-content-center d-flex">
                    <div className="row">
                        {DUMMY_PROFILES?.map((p) => (
                            <div className="col-lg-3 col-md-6 col-sm-6 col-12" key={p.id}>
                                <ProfileCard
                                    profile={p}
                                    busy={busyId === p.id}
                                    onBook={() => handleBook(p)}
                                />
                            </div>
                        ))}
                    </div>
                </div>


                {confirmation?.visible && (
                    <Confirmation
                        message={confirmation.message}
                        onClose={() => setConfirmation({ visible: false, message: '' })}
                    />
                )}
            </div>

        </>


    );
}
