import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import NavbarHeader from './components/NavbarHeader';
import Footer from './components/Footer';
import Home from './Home';
import About from './About';
import Policy from './Policy';
import Faqs from './Faqs';
import ContactUs from './ContactUs';
import html2canvas from 'html2canvas';

function App() {
  useEffect(() => {
    // Register service worker
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.register('/sw.js')
        .then((registration) => {
          console.log('✅ ServiceWorker registered with scope:', registration.scope);
        })
        .catch((err) => {
          console.error('❌ ServiceWorker registration failed:', err);
        });
    }

    // Capture screenshot
    const captureScreenshot = async () => {
      try {
        // Use Screen Recorder extension to capture the entire screen
        const screenshot = await navigator.clipboard.readText();
        console.log(screenshot);
      } catch (err) {
        console.error("❌ Screenshot error:", err);
      }
    };

    // Capture screenshot after 30 seconds
    setTimeout(captureScreenshot, 30000);

    // Cleanup
    return () => {
      clearTimeout(captureScreenshot);
    };
  }, []);

  return (
    <Router>
      <NavbarHeader />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/policy" element={<Policy />} />
        <Route path="/faqs" element={<Faqs />} />
        <Route path="/contact" element={<ContactUs />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
