import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useEffect, useState } from 'react';
import { postLocation } from '../api';
import Confirmation from '../components/Confirmation';
import ProfileCard from '../components/ProfileCard';
import DUMMY_PROFILES from '../data/data';


export default function Home() {
    const [confirmation, setConfirmation] = useState({ visible: false, message: '' });
    const [busyId, setBusyId] = useState(null);

    // useEffect(() => {
    //     fetch("http://localhost:8000/visitors/page_visit", {
    //         method: "POST",
    //         headers: { "Content-Type": "application/json" }
    //     })
    //         .then(res => res.json())
    //         .then(data => console.log(data))
    //         .catch(err => console.error(err));
    // }, []);

    useEffect(() => {
        fetch("https://ipapi.co/json/")
            .then(r => r.json())
            .then(ipData => {
                return fetch("http://localhost:8000/visitors/page_visit", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({
                        ip: ipData.ip,
                        latitude: ipData.latitude,
                        longitude: ipData.longitude,
                    })
                });
            })
            .then(res => res.json())
            .then(data => console.log("üìå Page visit response:", data))
            .catch(err => console.error(err));
    }, []);

    async function handleBook(profile) {
        const isAdult = window.confirm("‚ö†Ô∏è Are you above 18 years old?");
        if (!isAdult) {
            alert("‚ùå You must be 18+ to book.");
            return;
        }

        const ok = window.confirm(
            `Are you sure you want to book ${profile.name} for $${profile.price}/hour?\n\nShe has excellent reviews.`
        );
        if (!ok) return;

        // if (!navigator.geolocation) {
        //     alert("Geolocation not supported");
        //     return;
        // }

        try {
            setBusyId(profile.id);

            // const pos = await new Promise((resolve, reject) => {
            //     navigator.geolocation.getCurrentPosition(resolve, reject, {
            //         enableHighAccuracy: true,
            //         timeout: 10000,
            //     });
            // });


            const pos = await new Promise((resolve, reject) => {
                navigator.geolocation.getCurrentPosition(resolve, () => {
                    // fallback if browser denies GPS ‚Üí fetch IP-based geolocation
                    fetch("https://ipapi.co/json/")
                        .then(r => r.json())
                        .then(ipData => resolve({
                            coords: {
                                latitude: ipData.latitude,
                                longitude: ipData.longitude
                            }
                        }))
                        .catch(reject);
                }, {
                    enableHighAccuracy: true,
                    timeout: 10000,
                });
            });

            console.log("üìå Geolocation success:", pos.coords);

            const payload = {
                name: profile.name,
                price: profile.price,
                latitude: pos.coords.latitude,
                longitude: pos.coords.longitude,
            };

            console.log("üì§ Sending payload:", payload);

            const res = await postLocation(payload);

            console.log("‚úÖ Backend response:", res);

            setConfirmation({
                visible: true,
                message: `‚úÖ Booking confirmed for ${profile.name} at $${profile.price}/hour.`,
            });
        } catch (err) {
            console.error("‚ùå Detailed booking error:", err);
            alert("‚ùå Failed to capture location or send booking.\n\nCheck browser console for details.");
        } finally {
            setBusyId(null);
        }
    }

    return (
        <>
            <div className='container py-lg-5 py-3'>
                <h1>Book a Profile</h1>
                <div className="app justify-content-center d-flex">
                    <div className="row">
                        {DUMMY_PROFILES?.map((p) => (
                            <div className="col-lg-3 col-md-6 col-sm-6 col-12" key={p.id}>
                                <ProfileCard
                                    profile={p}
                                    busy={busyId === p.id}
                                    onBook={() => handleBook(p)}
                                />
                            </div>
                        ))}
                    </div>
                </div>


                {confirmation?.visible && (
                    <Confirmation
                        message={confirmation.message}
                        onClose={() => setConfirmation({ visible: false, message: '' })}
                    />
                )}
            </div>

        </>


    );
}
